import React from "react";

import "./assets/infoBox.svg";
import "./assets/infoIconClose.png";
import "./assets/infoIconMeltAnimation.png";
import "./styles/box.css";
import { TweenLite, TimelineMax, SteppedEase, TweenMax } from "gsap";

export default class Box extends React.Component {
	constructor(props) {
		super(props);
		this.infoBox = null;
		this.infoIcon = null;
		this.state = {
			isInfoHidden: true,
			iconStaus: "Running",
			openInfoBox: new TimelineMax({
				paused: true
			}),
			iconMelt: new TimelineMax({
				paused: true
			})
		};
		this.toggleInfo = this.toggleInfo.bind(this);
		this.showInfo = this.showInfo.bind(this);
		this.closeInfo = this.closeInfo.bind(this);
	}
	componentDidMount() {
		var self = this;
		TweenLite.set(this.infoBox, { scaleY: 0, transformOrigin: "50% 0%" });
		this.state.openInfoBox.to(this.infoBox, 0.5, {
			delay: 0.25,
			scaleY: 1,
			transformOrigin: "50% 0%",
			onComplete: function() {
				self.setState(state => ({
					iconStatus: "Open"
				}));
			}
		});
		this.state.openInfoBox.progress(1).progress(0);
		this.state.iconMelt.to(this.infoIcon, 0.5, {
			backgroundPosition: "-1400px",
			ease: SteppedEase.config(14),
			onComplete: function() {
				if (this.state.iconMelt.reversed()) {
					self.setState(state => ({
						iconStatus: "Closed"
					}));
				}
			}
		});
		this.state.iconMelt.progress(1).progress(0);
	}
	toggleInfo(e) {
		e.preventDefault();
		this.state.isInfoHidden ? this.showInfo() : this.closeInfo();
	}
	showInfo() {
		this.setState(state => ({
			isInfoHidden: false
		}));

		this.state.openInfoBox.play();
		this.state.iconMelt.play();
	}
	closeInfo() {
		this.setState(state => ({
			isInfoHidden: true
		}));
		this.state.openInfoBox.reverse();
		var self = this;
		TweenMax.delayedCall(0.25, function() {
			self.state.iconMelt.reverse();
		});
	}
	render() {
		return (
			<div className="box">
				<div className="boxTitle">
					<div className="boxTitleText">
						<h3>{this.props.boxTitle}</h3>
					</div>
					<div
						className="boxTitleInfo"
						onClick={this.toggleInfo}
						title={"More on " + this.props.boxTitle}
					>
						{(() => {
							switch (this.state.iconStatus) {
								case "Closed":
									console.log("Closed");
									return (
										<div
											src="url('./assets/infoIcon.svg')"
											className="infoIcon"
											ref={div => (this.infoIcon = div)}
										/>
									);
								case "Running":
									return (
										<div
											style="background: url('./assets/infoIconMeltAnimation.png')"
											className="infoIcon"
											ref={div => (this.infoIcon = div)}
										/>
									);
								case "Open":
									return (
										<div
											src="url('./assets/infoIconClosed.png')"
											className="infoIcon"
											ref={div => (this.infoIcon = div)}
										/>
									);
								default:
									return (
										<div
											src="url('./assets/infoIcon.svg')"
											className="infoIcon"
											ref={div => (this.infoIcon = div)}
										/>
									);
							}
						})()}
					</div>
				</div>
				<div
					className="boxInfoContent"
					ref={div => (this.infoBox = div)}
				>
					{this.props.boxInfoContent}
				</div>
				<div className="boxContent">{this.props.boxContent}</div>
			</div>
		);
	}
}
