import React from "react";
import "./styles/arpLite.css";
import vis from "vis";

import "vis/dist/vis.min.css";

export default class ArpLite extends React.Component {
	constructor(props) {
		super(props);
		var date = new Date();
		date.setHours(date.getHours() + 4 * (Math.random() < 0.2));
		var start = new Date(date);
		this.state = {
			visTimeline: null,
			items: new vis.DataSet([
				{ id: 1, content: "item 1", start: "5s" },
				{ id: 2, content: "item 2", start: "10s" },
				{ id: 3, content: "item 3", start: "15s" },
				{
					id: 4,
					content: "item 4",
					start: "20s",
					end: "25s"
				},
				{ id: 5, content: "item 5", start: "30s" },
				{ id: 6, content: "item 6", start: "40s" }
			])
		};
		this.visualWrapper = null;
	}
	componentDidMount() {
		var options = {
			width: "100%",
			height: "300px",
			max: "45",
			min: "0"
		};
		this.setState(state => ({
			visTimeline: new vis.Timeline(
				this.visualWrapper,
				this.state.items,
				options
			)
		}));
	}
	render() {
		return (
			<div className="ArpLiteBox">
				<div
					className="visualWrapper"
					ref={div => (this.visualWrapper = div)}
				/>
			</div>
		);
	}
}
