import React from "react";
import "./styles/arpLite.css";
import vis from "vis";

import "vis/dist/vis.min.css";

export default class ArpLite extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			visTimeline: null,
			items: new vis.DataSet([
				{ id: 1, type: "point", content: "item 1", start: new Date() }
			])
		};
		this.visualWrapper = null;
	}
	componentDidMount() {
		var options = {
			width: "100%",
			height: "300px",
			start: new Date(),
			end: new Date(new Date() + 180),
			min: new Date(),
			max: new Date(new Date() + 180),
			editable: true,
			format: {
				minorLabels: {
					hour: "h:mm A"
				}
			}
		};
		this.setState(state => ({
			visTimeline: new vis.Timeline(
				this.visualWrapper,
				this.state.items,
				options
			)
		}));
	}
	render() {
		return (
			<div className="ArpLiteBox">
				<div
					className="visualWrapper"
					ref={div => (this.visualWrapper = div)}
				/>
			</div>
		);
	}
}
