import React from "react";
import "./styles/arpLite.css";
import vis from "vis";

import "vis/dist/vis.min.css";

export default class ArpLite extends React.Component {
	constructor(props) {
		super(props);
		var date = new Date();
		date.setHours(date.getHours() + 4 * (Math.random() < 0.2));
		var start = new Date(date);
		this.state = {
			visTimeline: null,
			items: new vis.DataSet([
				{ id: 1, content: "item 1", start: "start" }
			])
		};
		this.visualWrapper = null;
	}
	componentDidMount() {
		var options = {
			width: "100%",
			height: "300px",
			start: new Date(),
			end: new Date(1000 * 60 * 60 * 24 + new Date().valueOf())
		};
		this.setState(state => ({
			visTimeline: new vis.Timeline(
				this.visualWrapper,
				this.state.items,
				options
			)
		}));
	}
	render() {
		return (
			<div className="ArpLiteBox">
				<div
					className="visualWrapper"
					ref={div => (this.visualWrapper = div)}
				/>
			</div>
		);
	}
}
