import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

class Column extends React.Component {
    render() {
        console.log(this.props.boxes);
        return (
            <div className="column">
                {this.props.boxes.map((item, index) => (
                    <Box boxTitle={item.title} boxContent={item.content} />
                ))}
            </div>
        );
    }
}

class Box extends React.Component {
    render() {
        return (
            <div className="box">
                <div className="boxTitle">{this.props.title}</div>
                <div className="boxContent">{this.props.content}</div>
            </div>
        );
    }
}

class Title extends React.Component {
    render() {
        return (
            <div className="title">
                <h1>{this.props.text}</h1>
            </div>
        );
    }
}

class Main extends React.Component {
    render() {
        var colOneBox,
            colTwoBox,
            colThreeBox = [];
        const boxCollection = [
            { title: "Box1", content: <p>Box 1 Data</p> },
            { title: "Box2", content: <p>Box 2 Data</p> },
            { title: "Box3", content: <p>Box 3 Data</p> },
            { title: "Box4", content: <p>Box 4 Data</p> },
            { title: "Box5", content: <p>Box 5 Data</p> },
            { title: "Box6", content: <p>Box 6 Data</p> },
            { title: "Box7", content: <p>Box 7 Data</p> },
            { title: "Box8", content: <p>Box 8 Data</p> }
        ];
        boxCollection.map((item, index) => {
            if (index % 3 === 0) colOneBox.push(boxCollection[index]);
            else if (index % 3 === 0) colOneBox.push(boxCollection[index]);
            if (index % 3 === 0) colOneBox.push(boxCollection[index]);
        });

        return (
            <div className="main">
                <Title text={"Daniel S. Abrahms"} />
                <div className="columnBox">
                    <Column boxes={colOneBox} />
                    <Column boxes={colTwoBox} />
                    <Column boxes={colThreeBox} />
                </div>
            </div>
        );
    }
}

// ========================================

ReactDOM.render(<Main />, document.getElementById("root"));
