import React from "react";
import "./styles/arpLite.css";
import vis from "vis";

import "../../node_modules/vis/dist/vis.min.css";

export default class ArpLite extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			visTimeline: null,
			items: new vis.DataSet([
				{ id: 1, content: "item 1", start: "2013-04-20" },
				{ id: 2, content: "item 2", start: "2013-04-14" },
				{ id: 3, content: "item 3", start: "2013-04-18" },
				{
					id: 4,
					content: "item 4",
					start: "2013-04-16",
					end: "2013-04-19"
				},
				{ id: 5, content: "item 5", start: "2013-04-25" },
				{ id: 6, content: "item 6", start: "2013-04-27" }
			])
		};
		this.visualWrapper = null;
	}
	componentDidMount() {
		var options = {};
		this.setState(state => ({
			visTimeline: new vis.Timeline(
				this.visualWrapper,
				this.state.items,
				options
			)
		}));
	}
	render() {
		return (
			<div className="ArpLiteBox">
				<div
					className="visualWrapper"
					ref={div => (this.visualWrapper = div)}
				/>
			</div>
		);
	}
}
