import React from "react";
import "./styles/arpLite.css";
import { TweenMax } from "gsap";
import { Button } from "react-bootstrap";

export default class ArpLite extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			audioSrc: "./assets/example.wav"
		};
		this.audioTrack = null;
		this.playTrack = this.playTrack.bind(this);
		this.pauseTrack = this.pauseTrack.bind(this);
		this.stopTrack = this.stopTrack.bind(this);
	}
	playTrack() {
		this.audioTrack.play();
	}
	pauseTrack() {
		this.audioTrack.pause();
	}
	stopTrack() {
		this.audioTrack.stop();
	}
	render() {
		return (
			<div className="ArpLiteBox">
				<audio
					src={this.state.audioSrc}
					ref={audio => (this.audioTrack = audio)}
				/>
				<Button onClick={this.playTrack}>Play</Button>
				<Button onClick={this.pauseTrack}>Pause</Button>
				<Button onClick={this.stopTrack}>Stop</Button>
				<div className="scrubber" />
			</div>
		);
	}
}
